    private fun addPinyins(initial: Char, chars: String) {
        for (char in chars) {
            PINYIN_INITIALS[char] = initial
        }
    }

    /**
     * 将中文转换为拼音首字母
     * @param text 中文文本
     * @return 拼音首字母（小写）
     */
    fun toPinyinInitials(text: String): String {
        val result = StringBuilder()
        for (char in text) {
            when {
                // 字母和数字直接保留
                char.isLetterOrDigit() && char.code < 128 -> {
                    result.append(char.lowercaseChar())
                }
                // 查找映射表
                PINYIN_INITIALS.containsKey(char) -> {
                    result.append(PINYIN_INITIALS[char])
                }
                // 其他中文字符，返回 '?' 表示未知
                char.code in 0x4E00..0x9FA5 -> {
                    result.append('?')
                }
                // 空格跳过
                char.isWhitespace() -> {
                    // 跳过空格
                }
            }
        }
        return result.toString()
    }
}
